import React, { useState } from 'react'

const ERROR_IMG_SRC =
  'data:image/svg+xml;base64,'

type ImageProps = React.ImgHTMLAttributes<HTMLImageElement> & {
  sizes?: string;
  srcSet?: string;
};

export function ImageWithFallback(props: ImageProps) {
  const [didError, setDidError] = useState(false)

  const handleError = () => {
    setDidError(true)
  }

  const { src, alt, style, className, loading, decoding, fetchPriority, sizes, srcSet, ...rest } = props

  return didError ? (
    <div
      className={`inline-block bg-gray-100 text-center align-middle ${className ?? ''}`}
      style={style}
    >
      <div className="flex items-center justify-center w-full h-full">
        <img src={ERROR_IMG_SRC} alt="Error loading image" {...rest} sizes={sizes} srcSet={srcSet} data-original-url={src} loading={loading ?? 'lazy'} decoding={decoding ?? 'async'} fetchPriority={fetchPriority ?? 'low'} />
      </div>
    </div>
  ) : (
    <img src={src} alt={alt} className={className} style={style} {...rest} onError={handleError} loading={loading ?? 'lazy'} decoding={decoding ?? 'async'} fetchPriority={fetchPriority ?? 'low'} sizes={sizes} srcSet={srcSet} />
  )
}
